import {React, useState} from 'react'

const ContactForm = (props) => {

    const [userName, setUserName] = useState('')
    const [userEmail, setUserEmail] = useState('')
    const [userNumber, setUserNumber] = useState('')
    const [job, setJob] = useState('')
    const [experience, setExperience] = useState('')
    const [skills, setSkills] = useState('')

    const handleChange = (e) => {

    }

    const handleJobChange = (e) => {
        setJob(e.target.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault()
    }

    return (
        <div>
            <form onSubmit={handleSubmit}> 
            <label>Full Name</label>
            <input type="text" name="userName" value={userName} onChange={handleChange}/><br/>
            <label>Email Address</label>
            <input type="text" name="userName" value={userEmail} onChange={handleChange}/><br/>
            <label>Contact Number</label>
            <input type="text" name="userName" value={userNumber} onChange={handleChange}/><br/>
            <label>Job Applying for</label>
            <select name="" value={job} onChange={handleJobChange}>
            <option value="">Select</option>
            <option value="Front-End Developer">Front-End Developer</option>
            <option value="Node.js Developer">Node.js Developer</option>
            <option value="MEAN Stack Developer">MEAN Stack Developer</option>
            <option value="FULL Stack Developer">FULL Stack Developer</option>
            </select> <br/>
            <label>Experience</label>
            <input type="text" name="experience" value={experience} onChange={handleChange}/><br/>
            <label>Technical Skills</label>
            <input type="text" name="skills" value={skills} onChange={handleChange}/><br/>
            <input type="submit" value="Send Application"/><br/>
            </form>




        </div>
    )
}

export default ContactForm
