import {React, useState} from 'react'
import validator from 'validator';
import axios from 'axios'


const ApplyForm = (props) => {

    const [userName, setUserName] = useState('')
    const [userEmail, setUserEmail] = useState('')
    const [userNumber, setUserNumber] = useState('')
    const [role, setRole] = useState('')
    const [experience, setExperience] = useState('')
    const [skills, setSkills] = useState('')
    const [formErrors, setFormErrors] = useState({})
    const errors = {}

    const handleChange = (e) => {
        if(e.target.name === "userName"){
            setUserName(e.target.value)
        } else if(e.target.name === "userEmail"){
            setUserEmail(e.target.value)
        } else if(e.target.name === "userNumber"){
            setUserNumber(e.target.value)
        } else if(e.target.name === "role"){
            setRole(e.target.value)
        } else if(e.target.name === "experience"){
            setExperience(e.target.value)
        } else if(e.target.name === "skills"){
            setSkills(e.target.value)
        }
    }

    const runValidations = () => {
        if(userName.trim().length === 0) {
            errors.userName = 'Name cannot be blank'
        }
        if(userEmail.trim().length === 0) {
            errors.userEmail = 'Email cannot be blank'
        } else if (!validator.isEmail(userEmail)) {
            errors.userEmail = 'Invalid Email Format'
        }
        if(userNumber.trim().length === 0) {
            errors.userNumber = 'Number cannot be blank'
        }
        if(role === '') {
            errors.role = 'Please select a Role'
        }
        if(experience.trim().length === 0) {
            errors.experience = 'Experience cannot be blank'
        }
        if(skills.trim().length === 0) {
            errors.skills = 'Skills cannot be blank'
        }   
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        runValidations()
        if (Object.keys(errors).length === 0){
            const formData = {
                name: userName, 
                email:userEmail, 
                phone: userNumber,
                skills: skills,
                jobTitle: role, 
                experience: experience}
                console.log(formData)

                axios.post("https://dct-application-form.herokuapp.com/users/application-form", formData)
                .then((response)=>
                console.log(response.data))
                .catch((err)=>{
                    console.log(err)
                });
                setUserName('')
                setUserEmail('')
                setUserNumber('')
                setRole('')
                setExperience('')
                setSkills('')
        } else {
            setFormErrors(errors)
        }
    }

    return (
        <div style={{background:'rgba(255, 255, 255, 0.6)', backdropFilter: 'blur(40px)', margin:'16px', padding:'16px', borderRadius:'16px', width:'50%'}}>
            <h1 style={{paddingBottom:'16px'}}>Apply for a Job</h1> 

            <form onSubmit={handleSubmit}> 
            <label>Full Name</label>
            <input type="text" name="userName" value={userName} onChange={handleChange}/><br/>
            {formErrors.userName && <p>{formErrors.userName}</p>} <br/>
            <label>Email Address</label>
            <input type="text" name="userEmail" value={userEmail} onChange={handleChange}/><br/>
            {formErrors.userEmail && <p>{formErrors.userEmail}</p>} <br/>
            <label>Contact Number</label>
            <input type="text" name="userNumber" value={userNumber} onChange={handleChange}/><br/>
            {formErrors.userNumber && <p>{formErrors.userNumber}</p>} <br/>
            <label>Job Applying for</label>
            <select name="role" value={role} onChange={handleChange}>
            <option value="">Select</option>
            <option value="Front-End Developer">Front-End Developer</option>
            <option value="Node.js Developer">Node.js Developer</option>
            <option value="MEAN Stack Developer">MEAN Stack Developer</option>
            <option value="FULL Stack Developer">FULL Stack Developer</option>
            </select> <br/>
            {formErrors.role && <p>{formErrors.role}</p>} <br/>
            <label>Experience</label>
            <input type="text" name="experience" value={experience} onChange={handleChange}/><br/>
            {formErrors.experience && <p>{formErrors.experience}</p>} <br/>
            <label>Technical Skills</label>
            <textarea name="skills" value={skills} onChange={handleChange}></textarea><br/>
            {formErrors.skills && <p>{formErrors.skills}</p>} <br/>
            <input type="submit" value="Send Application"/><br/>
            </form>




        </div>
    )
}

export default ApplyForm
