import {React, useState, useEffect} from 'react'
import DashTable from './DashTable'
import axios from 'axios'

const RoleSelect = (props) => {

    const [candidateData, setCandidateData] = useState([])

    useEffect(()=>{
        axios.get('https://dct-application-form.herokuapp.com/users/application-forms')
        .then((response)=>{
            const result = response.data
            setCandidateData(result)
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }, [])

    const handleSelect = (id) => {

        const result = {status: "shortlisted"}

        axios.put(`https://dct-application-form.herokuapp.com/users/application-form/update/${id}`, result)
        .then(response=>{
            const newData = response.data

            if(Object.keys(newData).length>0){
                const result = candidateData.map(ele=>{
                    if(ele._id===id){
                        return {...ele, ...newData}
                    }
                })
                console.log(result)
            }
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }

    const handleAll = () => {
        axios.get('https://dct-application-form.herokuapp.com/users/application-forms')
        .then((response)=>{
            const result = response.data
            setCandidateData(result)
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }

    const handleFront = () => {
        axios.get('https://dct-application-form.herokuapp.com/users/application-forms')
        .then((response)=>{
            const data = response.data
            const result = data.filter(ele=>ele.jobTitle==="Front-End Developer")
            setCandidateData(result)
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }
    const handleNode = () => {
        axios.get('https://dct-application-form.herokuapp.com/users/application-forms')
        .then((response)=>{
            const data = response.data
            const result = data.filter(ele=>ele.jobTitle==="Node.js Developer")
            setCandidateData(result)
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }

    const handleMean = () => {
        axios.get('https://dct-application-form.herokuapp.com/users/application-forms')
        .then((response)=>{
            const data = response.data
            const result = data.filter(ele=>ele.jobTitle==="MEAN Stack Developer")
            setCandidateData(result)
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }

    const handleFull = () => {
        axios.get('https://dct-application-form.herokuapp.com/users/application-forms')
        .then((response)=>{
            const data = response.data
            const result = data.filter(ele=>ele.jobTitle==="FULL Stack Developer")
            setCandidateData(result)
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }

    return (
        <div>
            <button onClick={handleAll}>All</button>
            <button onClick={handleFront}>Front-End Developer</button>
            <button onClick={handleNode}>Node.js Developer</button>
            <button onClick={handleMean}>MEAN Stack Developer</button>
            <button onClick={handleFull}>FULL Stack Developer</button>
            <DashTable candidateData={candidateData} handleSelect={handleSelect}/>
        </div>
    )
}

export default RoleSelect
