        })
    }


    return (
        <div style={{background:'rgba(255, 255, 255, 0.6)', backdropFilter: 'blur(40px)', margin:'16px', padding:'16px', borderRadius:'16px'}}>
            <table border='1'>
                <thead>
                    <tr style={{padding: '15px'}}>
                        <th>Name</th>
                        <th>Technical Skills</th>
                        <th>Experience</th>
                        <th>Applied Date</th>
                        <th>Status</th>
                        <th>View Details</th>
                        <th>Update Application Status</th>
                    </tr>
                </thead>
                <tbody>
                        {candidateData.length>0 && candidateData.map(ele=>{
                            return (
                                <tr key={ele._id}>
                                    <td>{ele.name}</td>
                                    <td>{ele.skills}</td>
                                    <td>{ele.experience}</td>
                                    <td>{new Date(ele.createdAt).getDate()}/
                                        {new Date(ele.createdAt).getMonth()+1}/
                                        {new Date(ele.createdAt).getFullYear()}
                                    </td>
                                    <td>{ele.status}</td>
                                    <td><button>View Details</button></td>
                                    <td>
                                        {ele.status === "rejected" && <button onClick={()=>{handleSelect(ele._id)}}>Select</button>} 
                                        {ele.status === "shortlisted" && <button onClick={()=>{handleReject(ele._id)}}>Reject</button>}
                                        {ele.status === "applied" && 
                                        <div>
                                            <button onClick={()=>{handleSelect(ele._id)}}>Select</button>
                                            <button onClick={()=>{handleReject(ele._id)}}>Reject</button>
                                        </div>
                                        }
                                    </td>               
                                </tr>
                            )
                        })}
                </tbody>
            </table>
        </div>
    )
}

export default DashTable
