import {React, useState, useEffect} from 'react'
import DashTable from './DashTable'
import axios from 'axios'

const RoleSelect = (props) => {

    const [candidateData, setCandidateData] = useState([])
    // const [toggle, setToggle] = useState(false)

    // const handleToggle = () => {
    //     setToggle(!toggle)
    // }

    useEffect(()=>{
        axios.get('https://dct-application-form.herokuapp.com/users/application-forms')
        .then((response)=>{
            const result = response.data
            setCandidateData(result)
            console.log(result)
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }, [])

    const updateApplication = (user) => {
        const result = candidateData.map((candidate) => {
          if (candidate._id === user._id) {
            return { ...candidate, ...user };
          } else {
            return { ...candidate };
          }
        });
        setCandidateData(result);
      };

    const handleAll = () => {
        axios.get('https://dct-application-form.herokuapp.com/users/application-forms')
        .then((response)=>{
            const result = response.data
            setCandidateData(result)
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }

    const handleFront = () => {
        axios.get('https://dct-application-form.herokuapp.com/users/application-forms')
        .then((response)=>{
            const data = response.data
            const result = data.filter(ele=>ele.jobTitle==="Front-End Developer")
            setCandidateData(result)
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }
    const handleNode = () => {
        axios.get('https://dct-application-form.herokuapp.com/users/application-forms')
        .then((response)=>{
            const data = response.data
            const result = data.filter(ele=>ele.jobTitle==="Node.js Developer")
            setCandidateData(result)
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }

    const handleMean = () => {
        axios.get('https://dct-application-form.herokuapp.com/users/application-forms')
        .then((response)=>{
            const data = response.data
            const result = data.filter(ele=>ele.jobTitle==="MEAN Stack Developer")
            setCandidateData(result)
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }

    const handleFull = () => {
        axios.get('https://dct-application-form.herokuapp.com/users/application-forms')
        .then((response)=>{
            const data = response.data
            const result = data.filter(ele=>ele.jobTitle==="FULL Stack Developer")
            setCandidateData(result)
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }

    return (
        <div className="nav nav-pills nav-fill">
            <div className="nav-item">
                <button className="nav-link active" aria-current="page" onClick={handleAll}>All</button>
            </div>
            <div>

            </div>
            <div>

            </div>
            <div>

            </div>
            <div>

            </div>

            <button onClick={handleNode}>Node.js Developer</button>
            <button onClick={handleMean}>MEAN Stack Developer</button>
            <button onClick={handleFull}>FULL Stack Developer</button>
            <DashTable candidateData={candidateData} updateApplication={updateApplication}/>
        </div>
    )
}

export default RoleSelect
