import React, { useState } from 'react'
import axios from 'axios'
import Swal from 'sweetalert2'


const DashTable = (props) => {

    const {candidateData, handleToggle} = props

    const [modal, setModal] = useState(false)

    const showModal = () => {
        setModal(!modal)
    }

    const hideModal = () => {
        setModal(modal)
    }

    const handleDetails = (id) => {
        axios.get(`https://dct-application-form.herokuapp.com/users/application-form/${id}`)
        .then((response)=>{
            const data = response.data

        })
        .catch((err)=>{
            console.log(err.message)
        })
    }

    const handleSelect = (id) => {

        const result = {status: "shortlisted"}

        axios.put(`https://dct-application-form.herokuapp.com/users/application-form/update/${id}`, result)
        .then(response=>{
            const newData = response.data
            console.log(newData)
            handleToggle()
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }

    const handleReject = (id) => {

        const result = {status: "rejected"}

        axios.put(`https://dct-application-form.herokuapp.com/users/application-form/update/${id}`, result)
        .then(response=>{
            console.log(response.data)
            const data = response.data
            handleToggle()
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }


    return (
        <div style={{background:'rgba(255, 255, 255, 0.6)', backdropFilter: 'blur(40px)', margin:'16px', padding:'16px', borderRadius:'16px'}}>
            <table border='1'>
                <thead>
                    <tr style={{padding: '15px'}}>
                        <th>Name</th>
                        <th>Technical Skills</th>
                        <th>Experience</th>
                        <th>Applied Date</th>
                        <th>Status</th>
                        <th>View Details</th>
                    </tr>
                </thead>
                <tbody>
                        {candidateData.length>0 && candidateData.map(ele=>{
                            return (
                                <tr key={ele._id}>
                                    <td>{ele.name}</td>
                                    <td>{ele.skills}</td>
                                    <td>{ele.experience}</td>
                                    <td>{new Date(ele.createdAt).getDate()}/
                                        {new Date(ele.createdAt).getMonth()+1}/
                                        {new Date(ele.createdAt).getFullYear()}
                                    </td>
                                    <td><button onClick={()=>{handleDetails(ele._id)}}>View Details</button></td>
                                    <td>
                                        {ele.status === "rejected" && <button onClick={()=>{handleSelect(ele._id)}}>Select</button>} 
                                        {ele.status === "shortlisted" && <button onClick={()=>{handleReject(ele._id)}}>Reject</button>}
                                        {ele.status === "applied" && 
                                        <div>
                                            <button onClick={()=>{handleSelect(ele._id)}}>Select</button>
                                            <button onClick={()=>{handleReject(ele._id)}}>Reject</button>
                                        </div>
                                        }
                                    </td>               
                                </tr>
                            )
                        })}
                </tbody>
            </table>
        </div>
    )
}

export default DashTable
