import React from 'react'

const DashTable = (props) => {

    const {candidateData} = props

    const handleSelect = (id) => {
        // const result = candidateData.map(ele=>{
        //     if(ele._id === id) {
        //         return {...ele, status:"shortlisted"}
        // }})
    }

    const handleReject = (id) => {

        const result = {status: "rejected"}

        axios.put(`https://dct-application-form.herokuapp.com/users/application-form/update/${id}`, result)
        .then(response=>{
            console.log(response.data)
            const data = response.data
            candidateData.forEach((ele)=>{
            if(ele._id === id) {
                return {...ele, status:data.status}
            }})
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }


    return (
        <div>
            <table style={{border:"2px solid black", padding: '15px'}}>
                <thead>
                    <tr style={{padding: '15px'}}>
                        <th>Name</th>
                        <th>Technical Skills</th>
                        <th>Experience</th>
                        <th>Applied Date</th>
                        <th>Status</th>
                        <th>View Details</th>
                        <th>Update Application Status</th>
                    </tr>
                </thead>
                <tbody>
                        {candidateData.length>0 && candidateData.map(ele=>{
                            return (
                                <tr key={ele._id}>
                                    <td>{ele.name}</td>
                                    <td>{ele.skills}</td>
                                    <td>{ele.experience}</td>
                                    <td>{new Date(ele.createdAt).getDate()}/
                                        {new Date(ele.createdAt).getMonth()+1}/
                                        {new Date(ele.createdAt).getFullYear()}
                                    </td>
                                    <td>{ele.status}</td>
                                    <td><button>View Details</button></td>
                                    <td>
                                        <button onClick={()=>{handleSelect(ele._id)}}>Select</button> 
                                        <button onClick={()=>{handleReject(ele._id)}}>Reject</button>
                                    </td>               
                                </tr>
                            )
                        })}
                </tbody>
            </table>
        </div>
    )
}

export default DashTable
